@api_router.get("/admin/tenants")
async def list_tenants(admin: SuperAdmin = Depends(get_super_admin)):
    """Liste toutes les casernes (actives et inactives) avec compteur de personnel"""
    # R√©cup√©rer TOUTES les casernes (pas de filtre) pour que le Super Admin puisse tout voir
    tenants_data = await db.tenants.find({}).to_list(100)
    
    # Ajouter le compteur d'employ√©s pour chaque tenant
    tenants_with_counts = []
    for tenant_data in tenants_data:
        # Supprimer _id (ObjectId non s√©rialisable)
        if '_id' in tenant_data:
            del tenant_data['_id']
        
        # Compter le nombre d'employ√©s
        nombre_employes = await db.users.count_documents({"tenant_id": tenant_data['id']})
        tenant_data['nombre_employes'] = nombre_employes
        
        # Normaliser le statut actif (g√©rer les deux champs actif et is_active)
        # Pour compatibilit√© avec anciennes et nouvelles donn√©es
        if 'is_active' not in tenant_data and 'actif' in tenant_data:
            tenant_data['is_active'] = tenant_data['actif']
        elif 'is_active' in tenant_data and 'actif' not in tenant_data:
            tenant_data['actif'] = tenant_data['is_active']
        
        tenants_with_counts.append(tenant_data)
    
    return tenants_with_counts

@api_router.get("/admin/stats")
async def get_global_stats(admin: SuperAdmin = Depends(get_super_admin)):
    """Statistiques globales avec calcul des revenus mensuels"""
    # R√©cup√©rer tous les tenants pour g√©rer les deux champs actif et is_active
    tous_tenants = await db.tenants.find({}).to_list(100)
    
    total_casernes_actives = 0
    total_casernes_inactives = 0
    tenants_actifs = []
    
    # Analyser chaque tenant pour d√©terminer son statut
    for tenant in tous_tenants:
        # Un tenant est actif si actif=True OU is_active=True
        is_active = tenant.get('actif', False) or tenant.get('is_active', False)
        
        if is_active:
            total_casernes_actives += 1
            tenants_actifs.append(tenant)
        else:
            total_casernes_inactives += 1
    
    # Calculer les revenus mensuels
    revenus_mensuels = 0
    total_pompiers = 0
    details_revenus = []
    
    for tenant in tenants_actifs:
        # Exclure la caserne "d√©monstration" du calcul des revenus (compte d√©mo client)
        tenant_slug = tenant.get('slug', '').lower()
        tenant_nom = tenant.get('nom', '').lower()
        
        is_demo = 'demonstration' in tenant_slug or 'demonstration' in tenant_nom or 'demo' in tenant_slug
        
        # Compter les pompiers de cette caserne
        user_count = await db.users.count_documents({"tenant_id": tenant["id"]})
        
        # Ajouter au total uniquement si ce n'est pas une caserne de d√©mo
        if not is_demo:
            total_pompiers += user_count
        
        # D√©terminer le prix par pompier selon le palier
        if user_count <= 30:
            prix_par_pompier = 12
        elif user_count <= 50:
            prix_par_pompier = 20
        else:
            prix_par_pompier = 27
        
        # Calculer le revenu pour cette caserne (0 si d√©mo)
        revenu_caserne = 0 if is_demo else (user_count * prix_par_pompier)
        revenus_mensuels += revenu_caserne
        
        details_revenus.append({
            "caserne": tenant["nom"],
            "pompiers": user_count,
            "prix_par_pompier": prix_par_pompier if not is_demo else 0,
            "revenu_mensuel": revenu_caserne,
            "is_demo": is_demo
        })
    
    return {
        "casernes_actives": total_casernes_actives,
        "casernes_inactives": total_casernes_inactives,
        "total_pompiers": total_pompiers,
        "revenus_mensuels": revenus_mensuels,
        "details_par_caserne": details_revenus
    }

# ==================== AUDIT LOGS ROUTES ====================

@api_router.get("/admin/audit-logs")
async def get_audit_logs(
    admin: SuperAdmin = Depends(get_super_admin),
    limit: int = 50,
    offset: int = 0,
    action: str = None,
    tenant_slug: str = None,
    admin_email: str = None
):
    """
    R√©cup√®re le journal d'audit des actions super-admin
    
    Param√®tres de filtrage optionnels:
    - action: Filtrer par type d'action (login, tenant_access, tenant_create, etc.)
    - tenant_slug: Filtrer par tenant
    - admin_email: Filtrer par super-admin
    """
    query = {}
    
    if action:
        query["action"] = action
    if tenant_slug:
        query["tenant_slug"] = tenant_slug
    if admin_email:
        query["admin_email"] = admin_email
    
    # R√©cup√©rer les logs avec pagination (plus r√©cents en premier)
    logs = await db.audit_logs.find(query, {"_id": 0}).sort("created_at", -1).skip(offset).limit(limit).to_list(limit)
    
    # Compter le total pour la pagination
    total = await db.audit_logs.count_documents(query)
    
    return {
        "logs": logs,
        "total": total,
        "limit": limit,
        "offset": offset
    }


@api_router.get("/admin/audit-logs/summary")
async def get_audit_logs_summary(admin: SuperAdmin = Depends(get_super_admin)):
    """
    R√©sum√© des actions d'audit (derni√®res 24h, 7 jours, 30 jours)
    """
    from datetime import timedelta
    
    now = datetime.now(timezone.utc)
    last_24h = now - timedelta(hours=24)
    last_7d = now - timedelta(days=7)
    last_30d = now - timedelta(days=30)
    
    # Compteurs par p√©riode
    count_24h = await db.audit_logs.count_documents({"created_at": {"$gte": last_24h}})
    count_7d = await db.audit_logs.count_documents({"created_at": {"$gte": last_7d}})
    count_30d = await db.audit_logs.count_documents({"created_at": {"$gte": last_30d}})
    
    # Actions par type (30 derniers jours)
    pipeline = [
        {"$match": {"created_at": {"$gte": last_30d}}},
        {"$group": {"_id": "$action", "count": {"$sum": 1}}},
        {"$sort": {"count": -1}}
    ]
    actions_by_type = await db.audit_logs.aggregate(pipeline).to_list(100)
    
    # Derniers acc√®s par tenant (30 derniers jours)
    pipeline_tenants = [
        {"$match": {"created_at": {"$gte": last_30d}, "tenant_slug": {"$ne": None}}},
        {"$group": {
            "_id": "$tenant_slug",
            "tenant_nom": {"$first": "$tenant_nom"},
            "last_access": {"$max": "$created_at"},
            "access_count": {"$sum": 1}
        }},
        {"$sort": {"last_access": -1}},
        {"$limit": 10}
    ]
    tenants_accessed = await db.audit_logs.aggregate(pipeline_tenants).to_list(10)
    
    # Activit√© par super-admin (30 derniers jours)
    pipeline_admins = [
        {"$match": {"created_at": {"$gte": last_30d}}},
        {"$group": {
            "_id": "$admin_email",
            "admin_nom": {"$first": "$admin_nom"},
            "action_count": {"$sum": 1},
            "last_action": {"$max": "$created_at"}
        }},
        {"$sort": {"action_count": -1}}
    ]
    admins_activity = await db.audit_logs.aggregate(pipeline_admins).to_list(100)
    
    return {
        "counts": {
            "last_24h": count_24h,
            "last_7d": count_7d,
            "last_30d": count_30d
        },
        "actions_by_type": [{"action": a["_id"], "count": a["count"]} for a in actions_by_type],
        "tenants_accessed": [
            {
                "tenant_slug": t["_id"],
                "tenant_nom": t.get("tenant_nom"),
                "last_access": t["last_access"].isoformat() if t.get("last_access") else None,
                "access_count": t["access_count"]
            }
            for t in tenants_accessed
        ],
        "admins_activity": [
            {
                "admin_email": a["_id"],
                "admin_nom": a.get("admin_nom"),
                "action_count": a["action_count"],
                "last_action": a["last_action"].isoformat() if a.get("last_action") else None
            }
            for a in admins_activity
        ]
    }

