# ===== GESTION DU MAT√âRIEL =====

@api_router.get("/{tenant_slug}/materiel")
async def get_materiel(
    tenant_slug: str,
    categorie_id: Optional[str] = None,
    etat: Optional[str] = None,
    localisation_type: Optional[str] = None,
    alerte_stock: bool = False,
    alerte_expiration: bool = False,
    current_user: User = Depends(get_current_user)
):
    """R√©cup√©rer le mat√©riel avec filtres"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    query = {"tenant_id": tenant.id}
    
    if categorie_id:
        query["categorie_id"] = categorie_id
    
    if etat:
        query["etat"] = etat
    
    if localisation_type:
        query["localisation_type"] = localisation_type
    
    if alerte_stock:
        # Items o√π quantit√©_stock <= quantit√©_minimum
        query["$expr"] = {"$lte": ["$quantite_stock", "$quantite_minimum"]}
    
    materiel = await db.materiel.find(query, {"_id": 0}).to_list(10000)
    
    # Filtrer les items avec expiration proche si demand√©
    if alerte_expiration:
        today = datetime.now(timezone.utc).date()
        materiel_filtre = []
        for item in materiel:
            if item.get('date_expiration'):
                try:
                    date_exp = datetime.strptime(item['date_expiration'], "%Y-%m-%d").date()
                    jours_restants = (date_exp - today).days
                    if jours_restants <= 30:  # Alerte 30 jours avant expiration
                        materiel_filtre.append(item)
                except:
                    pass
        materiel = materiel_filtre
    
    return materiel

