# ===== MOUVEMENTS DE STOCK =====

@api_router.post("/{tenant_slug}/materiel/{materiel_id}/mouvement")
async def create_mouvement_stock(
    tenant_slug: str,
    materiel_id: str,
    mouvement: MouvementStockCreate,
    current_user: User = Depends(get_current_user)
):
    """Enregistrer un mouvement de stock (tous les utilisateurs pour inventaire)"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    # R√©cup√©rer le mat√©riel
    item = await db.materiel.find_one({
        "id": materiel_id,
        "tenant_id": tenant.id
    })
    
    if not item:
        raise HTTPException(status_code=404, detail="Mat√©riel non trouv√©")
    
    # V√©rifier les permissions selon le type de mouvement
    if mouvement.type_mouvement != "inventaire":
        # Seuls admin/superviseur peuvent faire des entr√©es/sorties/ajustements
        if current_user.role not in ['admin', 'superviseur']:
            raise HTTPException(
                status_code=403,
                detail="Permission refus√©e - Seuls les inventaires sont autoris√©s pour ce r√¥le"
            )
    
    # Calculer les quantit√©s
    quantite_avant = item['quantite_stock']
    
    if mouvement.type_mouvement == "inventaire":
        # Pour un inventaire, la quantit√© est la nouvelle valeur absolue
        quantite_apres = mouvement.quantite
        quantite_mouvement = quantite_apres - quantite_avant
    else:
        # Pour les autres, c'est un delta
        quantite_mouvement = mouvement.quantite
        quantite_apres = quantite_avant + quantite_mouvement
    
    # V√©rifier que la quantit√© finale n'est pas n√©gative
    if quantite_apres < 0:
        raise HTTPException(
            status_code=400,
            detail=f"Stock insuffisant (actuel: {quantite_avant}, demand√©: {-quantite_mouvement})"
        )
    
    # Cr√©er le mouvement
    mouvement_obj = MouvementStock(
        tenant_id=tenant.id,
        materiel_id=materiel_id,
        type_mouvement=mouvement.type_mouvement,
        quantite=quantite_mouvement,
        quantite_avant=quantite_avant,
        quantite_apres=quantite_apres,
        raison=mouvement.raison,
        reference=mouvement.reference,
        effectue_par=f"{current_user.prenom} {current_user.nom}",
        effectue_par_id=current_user.id,
        notes=mouvement.notes
    )
    
    await db.mouvements_stock.insert_one(mouvement_obj.dict())
    
    # Mettre √† jour la quantit√© du mat√©riel
    await db.materiel.update_one(
        {"id": materiel_id, "tenant_id": tenant.id},
        {
            "$set": {
                "quantite_stock": quantite_apres,
                "cout_total": quantite_apres * item['cout_unitaire'],
                "updated_at": datetime.now(timezone.utc)
            }
        }
    )
    
    return {
        "message": "Mouvement enregistr√© avec succ√®s",
        "id": mouvement_obj.id,
        "quantite_avant": quantite_avant,
        "quantite_apres": quantite_apres
    }

