@api_router.put("/{tenant_slug}/materiel/maintenances/{maintenance_id}")
async def update_maintenance(
    tenant_slug: str,
    maintenance_id: str,
    maintenance: MaintenanceMaterielUpdate,
    current_user: User = Depends(get_current_user)
):
    """Mettre √† jour une maintenance (admin/superviseur uniquement)"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    if current_user.role not in ['admin', 'superviseur']:
        raise HTTPException(status_code=403, detail="Permission refus√©e - Admin/Superviseur requis")
    
    update_data = {k: v for k, v in maintenance.dict().items() if v is not None}
    if not update_data:
        return {"message": "Aucune modification"}
    
    update_data['updated_at'] = datetime.now(timezone.utc)
    
    result = await db.maintenances_materiel.update_one(
        {"id": maintenance_id, "tenant_id": tenant.id},
        {"$set": update_data}
    )
    
    if result.matched_count == 0:
        raise HTTPException(status_code=404, detail="Maintenance non trouv√©e")
    
    # Si la maintenance est termin√©e, mettre √† jour la date de derni√®re maintenance
    if update_data.get('statut') == 'terminee' and update_data.get('date_fin'):
        maintenance_doc = await db.maintenances_materiel.find_one({
            "id": maintenance_id,
            "tenant_id": tenant.id
        })
        
        if maintenance_doc:
            await db.materiel.update_one(
                {"id": maintenance_doc['materiel_id'], "tenant_id": tenant.id},
                {
                    "$set": {
                        "derniere_maintenance": update_data['date_fin'],
                        "date_prochaine_maintenance": update_data.get('prochaine_maintenance'),
                        "etat": "bon",  # Remettre en bon √©tat apr√®s maintenance
                        "updated_at": datetime.now(timezone.utc)
                    }
                }
            )
    
    return {"message": "Maintenance mise √† jour avec succ√®s"}


