@api_router.get("/{tenant_slug}/equipements/alertes")
async def get_alertes_equipements_route(
    tenant_slug: str,
    current_user: User = Depends(get_current_user)
):
    """R√©cup√©rer les alertes actives pour les √©quipements"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    parametres = await db.parametres_equipements.find_one({"tenant_id": tenant.id}, {"_id": 0})
    
    jours_maintenance = parametres.get('jours_alerte_maintenance', 30) if parametres else 30
    jours_expiration = parametres.get('jours_alerte_expiration', 30) if parametres else 30
    jours_fin_vie = parametres.get('jours_alerte_fin_vie', 90) if parametres else 90
    
    today = datetime.now(timezone.utc).date()
    date_limite_maintenance = (today + timedelta(days=jours_maintenance)).isoformat()
    date_limite_fin_vie = (today + timedelta(days=jours_fin_vie)).isoformat()
    
    alertes = {"maintenance": [], "expiration": [], "fin_vie": [], "reparation": [], "stock_bas": []}
    
    # Alertes maintenance
    equipements_maintenance = await db.equipements.find({
        "tenant_id": tenant.id,
        "date_prochaine_maintenance": {"$lte": date_limite_maintenance, "$ne": ""}
    }, {"_id": 0}).to_list(1000)
    
    for eq in equipements_maintenance:
        try:
            jours_restants = (datetime.fromisoformat(eq["date_prochaine_maintenance"]).date() - today).days
            alertes["maintenance"].append({
                "equipement_id": eq["id"], "code_unique": eq["code_unique"], "nom": eq["nom"],
                "categorie": eq.get("categorie_nom", ""), "date_prochaine": eq["date_prochaine_maintenance"],
                "jours_restants": jours_restants
            })
        except:
            pass
    
    # Alertes fin de vie
    equipements_fin_vie = await db.equipements.find({
        "tenant_id": tenant.id,
        "date_fin_vie": {"$lte": date_limite_fin_vie, "$ne": ""}
    }, {"_id": 0}).to_list(1000)
    
    for eq in equipements_fin_vie:
        try:
            alertes["fin_vie"].append({
                "equipement_id": eq["id"], "code_unique": eq["code_unique"], "nom": eq["nom"],
                "categorie": eq.get("categorie_nom", ""), "date_fin_vie": eq["date_fin_vie"],
                "jours_restants": (datetime.fromisoformat(eq["date_fin_vie"]).date() - today).days
            })
        except:
            pass
    
    # Alertes r√©paration
    equipements_reparation = await db.equipements.find({
        "tenant_id": tenant.id, "etat": {"$in": ["a_reparer", "en_reparation"]}
    }, {"_id": 0}).to_list(1000)
    
    for eq in equipements_reparation:
        alertes["reparation"].append({
            "equipement_id": eq["id"], "code_unique": eq["code_unique"], "nom": eq["nom"],
            "categorie": eq.get("categorie_nom", ""), "etat": eq["etat"]
        })
    
    return {
        "alertes": alertes,
        "totaux": {
            "maintenance": len(alertes["maintenance"]), "expiration": len(alertes["expiration"]),
            "fin_vie": len(alertes["fin_vie"]), "reparation": len(alertes["reparation"]),
            "stock_bas": len(alertes["stock_bas"]),
            "total": sum(len(v) for v in alertes.values())
        },
        "parametres": {"jours_alerte_maintenance": jours_maintenance, "jours_alerte_expiration": jours_expiration, "jours_alerte_fin_vie": jours_fin_vie}
    }


@api_router.post("/{tenant_slug}/equipements/alertes/recalculer")
async def recalculer_alertes_route(
    tenant_slug: str,
    current_user: User = Depends(get_current_user)
):
    """Recalculer toutes les alertes d'√©quipements (admin uniquement)"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    if current_user.role != 'admin':
        raise HTTPException(status_code=403, detail="Permission refus√©e - Admin requis")
    
    await db.equipements.update_many(
        {"tenant_id": tenant.id},
        {"$set": {"alerte_maintenance": False, "alerte_expiration": False, "alerte_fin_vie": False, "alerte_reparation": False, "alerte_stock_bas": False}}
    )
    
    alertes = await get_alertes_equipements_route(tenant_slug, current_user)
    
    return {"message": "Alertes recalcul√©es avec succ√®s", "totaux": alertes["totaux"]}


