@api_router.post("/{tenant_slug}/points-eau")
async def create_point_eau(
    tenant_slug: str,
    point_data: PointEauCreate,
    current_user: User = Depends(get_current_user)
):
    """Cr√©er un nouveau point d'eau"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    # V√©rifier permissions (admin/superviseur)
    if current_user.role not in ['admin', 'superviseur']:
        raise HTTPException(status_code=403, detail="Permission refus√©e - Admin/Superviseur requis")
    
    point_dict = point_data.dict()
    point_dict['id'] = str(uuid.uuid4())
    point_dict['tenant_id'] = tenant.id
    point_dict['created_by_id'] = current_user.id
    point_dict['created_at'] = datetime.now(timezone.utc).isoformat()
    point_dict['updated_at'] = datetime.now(timezone.utc).isoformat()
    
    await db.points_eau.insert_one(point_dict)
    
    return {"message": "Point d'eau cr√©√© avec succ√®s", "id": point_dict['id']}

@api_router.put("/{tenant_slug}/points-eau/{point_id}")
async def update_point_eau(
    tenant_slug: str,
    point_id: str,
    point_update: PointEauUpdate,
    current_user: User = Depends(get_current_user)
):
    """Mettre √† jour un point d'eau"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    # V√©rifier permissions
    if current_user.role not in ['admin', 'superviseur']:
        raise HTTPException(status_code=403, detail="Permission refus√©e - Admin/Superviseur requis")
    
    update_data = {k: v for k, v in point_update.dict().items() if v is not None}
    if not update_data:
        return {"message": "Aucune modification"}
    
    update_data['updated_at'] = datetime.now(timezone.utc).isoformat()
    
    result = await db.points_eau.update_one(
        {"id": point_id, "tenant_id": tenant.id},
        {"$set": update_data}
    )
    
    if result.matched_count == 0:
        raise HTTPException(status_code=404, detail="Point d'eau non trouv√©")
    
    return {"message": "Point d'eau mis √† jour avec succ√®s"}

@api_router.delete("/{tenant_slug}/points-eau/{point_id}")
async def delete_point_eau(
    tenant_slug: str,
    point_id: str,
    current_user: User = Depends(get_current_user)
):
    """Supprimer un point d'eau"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    # V√©rifier permissions (admin uniquement)
    if current_user.role != 'admin':
        raise HTTPException(status_code=403, detail="Permission refus√©e - Admin requis")
    
    result = await db.points_eau.delete_one(
        {"id": point_id, "tenant_id": tenant.id}
    )
    
    if result.deleted_count == 0:
        raise HTTPException(status_code=404, detail="Point d'eau non trouv√©")
    
    return {"message": "Point d'eau supprim√© avec succ√®s"}

# Endpoints pour les inspections de bornes s√®ches
@api_router.post("/{tenant_slug}/points-eau/{point_id}/inspections")
async def create_inspection_borne_seche(
    tenant_slug: str,
    point_id: str,
    inspection_data: dict,
    current_user: User = Depends(get_current_user)
):
    """Cr√©er une nouvelle inspection de borne s√®che"""
    tenant = await get_tenant_from_slug(tenant_slug)
    
    # V√©rifier que le point d'eau existe
    point = await db.points_eau.find_one(
        {"id": point_id, "tenant_id": tenant.id}
    )
    if not point:
        raise HTTPException(status_code=404, detail="Point d'eau non trouv√©")
    
    # Cr√©er l'inspection avec tous les champs du formulaire
    inspection = {
        "id": str(uuid.uuid4()),
        "point_eau_id": point_id,
        "tenant_id": tenant.id,
        "date_inspection": inspection_data.get("date_inspection"),
        "etat_trouve": inspection_data.get("etat_trouve"),
        "statut_inspection": inspection_data.get("statut_inspection", "conforme"),
        "notes": inspection_data.get("notes", ""),
        "photos": inspection_data.get("photos", []),
        "nom_pompier": inspection_data.get("nom_pompier", ""),
        "prenom_pompier": inspection_data.get("prenom_pompier", ""),
        "nom_inspecteur": inspection_data.get("nom_pompier", ""),
        "prenom_inspecteur": inspection_data.get("prenom_pompier", ""),
        "inspecteur_id": current_user.id,
        "temperature_exterieure": inspection_data.get("temperature_exterieure"),
        "temps_amorcage": inspection_data.get("temps_amorcage"),
        "resultats": {
            "accessibilite_borne": inspection_data.get("accessibilite_borne", []),
            "joint_present": inspection_data.get("joint_present", "conforme"),
            "joint_bon_etat": inspection_data.get("joint_bon_etat", "conforme"),
            "site_accessible": inspection_data.get("site_accessible", "conforme"),
            "site_deneige": inspection_data.get("site_deneige", "conforme"),
            "vanne_storz": inspection_data.get("vanne_storz", "conforme"),
            "vanne_6_pouces": inspection_data.get("vanne_6_pouces", "conforme"),
            "vanne_4_pouces": inspection_data.get("vanne_4_pouces", "conforme"),
            "niveau_eau": inspection_data.get("niveau_eau", "conforme"),
            "pompage_continu": inspection_data.get("pompage_continu", "conforme"),
            "cavitation": inspection_data.get("cavitation", "conforme")
        },
        "created_at": datetime.now(timezone.utc).isoformat()
    }
    
    await db.inspections_bornes_seches.insert_one(inspection)
    
    # Incr√©menter le compteur d'inspections
    await db.points_eau.update_one(
        {"id": point_id, "tenant_id": tenant.id},
        {"$inc": {"nombre_inspections": 1}}
    )
    
    # Mettre √† jour le point d'eau avec la derni√®re inspection
    etat_trouve = inspection_data.get("etat_trouve", "conforme")
    update_data = {
        "derniere_inspection_date": inspection_data.get("date_inspection"),
        "statut_inspection": inspection_data.get("statut_inspection", "conforme"),
        "updated_at": datetime.now(timezone.utc).isoformat()
    }
    
    # Si d√©faut d√©tect√©, marquer la borne comme hors service
    if etat_trouve == "a_refaire":
        update_data["etat"] = "hors_service"
    elif etat_trouve == "conforme":
        update_data["etat"] = "fonctionnelle"
    
    await db.points_eau.update_one(
        {"id": point_id, "tenant_id": tenant.id},
        {"$set": update_data}
    )
    
    # Si d√©faut d√©tect√©, envoyer email aux admins/superviseurs
    if etat_trouve == "a_refaire":
        print(f"üö® DEBUG: D√©faut d√©tect√© - etat_trouve = {etat_trouve}")
        # R√©cup√©rer les param√®tres d'email
        parametres = tenant.parametres if hasattr(tenant, 'parametres') and tenant.parametres else {}
        print(f"üö® DEBUG: Param√®tres tenant = {parametres}")
        user_ids_ou_emails = parametres.get('emails_notifications_bornes_seches', [])
        print(f"üö® DEBUG: User IDs ou Emails bruts = {user_ids_ou_emails}")
        
        # Convertir les user IDs en adresses email si n√©cessaire
        emails_notifications = []
        if user_ids_ou_emails:
            for item in user_ids_ou_emails:
                # V√©rifier si c'est un email (contient @) ou un user ID
                if '@' in str(item):
                    # C'est d√©j√† une adresse email
                    emails_notifications.append(item)
                else:
                    # C'est un user ID, r√©cup√©rer l'email
                    try:
                        user = await db.users.find_one(
                            {"id": item, "tenant_id": tenant.id},
                            {"_id": 0, "email": 1}
                        )
                        if user and user.get('email'):
                            emails_notifications.append(user['email'])
                            print(f"‚úÖ DEBUG: User ID {item} ‚Üí Email {user['email']}")
                        else:
                            print(f"‚ö†Ô∏è DEBUG: User ID {item} non trouv√©")
                    except Exception as e:
                        print(f"‚ùå DEBUG: Erreur r√©cup√©ration user {item}: {e}")
        
        print(f"üö® DEBUG: Emails finaux pour notification = {emails_notifications}")
        
        if emails_notifications:
            print(f"üö® DEBUG: Tentative d'envoi email √† {len(emails_notifications)} destinataire(s)")
            # Envoyer email de notification
            from utils.emails import send_defaut_borne_email
            try:
                result = await send_defaut_borne_email(
                    tenant_slug=tenant_slug,
                    borne=point,
                    inspection=inspection,
                    inspecteur=f"{inspection_data.get('prenom_pompier')} {inspection_data.get('nom_pompier')}",
                    emails=emails_notifications
                )
                print(f"‚úÖ DEBUG: R√©sultat envoi email = {result}")
                logging.info(f"Email de notification envoy√© avec succ√®s pour la borne {point_id}")
            except Exception as e:
                print(f"‚ùå DEBUG: Erreur envoi email - {str(e)}")
                logging.error(f"Erreur envoi email d√©faut borne: {e}")
        else:
            print(f"‚ö†Ô∏è DEBUG: Aucun email valide trouv√© apr√®s conversion")
            logging.warning(f"Aucun email de notification valide configur√© pour les d√©fauts de bornes s√®ches")
    
    return {"message": "Inspection enregistr√©e avec succ√®s", "id": inspection["id"]}

